changeSet.sql:
CREATE TABLE ArtifactGroup ( name varchar(255) NOT NULL, commonName varchar(255) DEFAULT NULL, description varchar(255) DEFAULT NULL, PRIMARY KEY (name) );
CREATE TABLE ArtifactProduct ( name varchar(255) NOT NULL, commonName varchar(255) DEFAULT NULL, description varchar(255) DEFAULT NULL, group_name varchar(255) DEFAULT NULL, PRIMARY KEY (name), FOREIGN KEY (group_name) REFERENCES ArtifactGroup(name) );
CREATE TABLE PlayerStats ( name varchar(255) NOT NULL, countryId varchar(255) DEFAULT NULL, highScore integer DEFAULT NULL, createdOn date, PRIMARY KEY (name) ); CREATE TABLE PlayerCountry ( id varchar(255) NOT NULL, isoCode varchar(255) DEFAULT NULL, PRIMARY KEY (id) ); CREATE TABLE ArtifactVersion ( name varchar(255) NOT NULL, createdAt timestamp DEFAULT NULL, artifact_name varchar(255) DEFAULT NULL, PRIMARY KEY (name), FOREIGN KEY (artifact_name) REFERENCES ArtifactProduct(name) );
CREATE TABLE AsyncQuery ( id varchar(36) NOT NULL, principalName varchar(255), query varchar(1024) NOT NULL, queryType int NOT NULL, status int NOT NULL, createdOn timestamp NOT NULL, updatedOn timestamp NOT NULL, naturalkey varchar(40) NOT NULL, PRIMARY KEY (id) );
CREATE TABLE AsyncQueryResult ( id varchar(36) NOT NULL, contentLength integer NOT NULL, responseBody varchar(10485760) NOT NULL, status integer NOT NULL, createdOn timestamp NOT NULL, updatedOn timestamp NOT NULL, query_id varchar(36) NOT NULL, naturalkey varchar(40) NOT NULL, PRIMARY KEY (id), FOREIGN KEY (query_id) REFERENCES AsyncQuery(id) );

